// {{{ NX.Date.between

/**
 * @class NX.Date
 */

/**
 * @method between
 *
 * "write it"
 *
 * @param o {Object} xxx
 * @return {Object}
 */
module.exports = function(date, start, end) {

    var t = date.getTime();

    return start.getTime() <= t && t <= end.getTime();
};

// }}}

