// {{{ NX.Date.formatContainsHourInfo

/**
 * @class NX.Date
 */

/**
 * @method formatContainsHourInfo
 *
 * "write it"
 *
 * @param o {Object} xxx
 * @return {Object}
 */
module.exports = (function(){

    var stripEscapeRe = /(\\.)/g,
        hourInfoRe = /([gGhHisucUOPZ]|MS)/;

    return function(format) {
        return hourInfoRe.test(format.replace(stripEscapeRe, ''));
    };

})();

// }}}

