// {{{ NX.Date.getFirstDateOfMonth

/**
 * @class NX.Date
 */

/**
 * @method getFirstDateOfMonth
 *
 * "write it"
 *
 * @param o {Object} xxx
 * @return {Object}
 */
module.exports = function(date) {
    return new Date(date.getFullYear(), date.getMonth(), 1);
};

