// {{{ NX.Date.getSuffix

/**
 * @class NX.Date
 */

/**
 * @method getSuffix
 *
 * "write it"
 *
 * @param o {Object} xxx
 * @return {Object}
 */
module.exports = function(date) {

    switch(date.getDate()) {
        case 1:
        case 21:
        case 31:
           return "st";
        case 2:
        case 22:
            return "nd";
        case 3:
        case 23:
            return "rd";
        default:
            return "th";
    }

};

// }}}

