// {{{ NX.Date.getTimezone

/**
 * @class NX.Date
 */

/**
 * @method getTimezone
 *
 * "write it"
 *
 * @param o {Object} xxx
 * @return {Object}
 */
module.exports = function(date) {
    return date.toString().replace(/^.* (?:\((.*)\)|([A-Z]{1,4})(?:[\-+][0-9]{4})?(?: -?\d+)?)$/, "$1$2").replace(/[^A-Z]/g, "");
};

// }}}

