// {{{ NX.applyIf

/**
 * @class NX
 */

/**
 * @method applyIf
 *
 * プロパティ値が存在しないものに対して、設定されたコンフィグオブジェクトをオブジェクトにコピーします。
 * 既に存在するプロパティは上書きしません。
 *
 * @param o {Object} 適用されるオブジェクト
 * @param config {Object} 適用するオブジェクト
 * @return {Object} 適用後のオブジェクト
 */
module.exports = function(o, config) {

    var prop;

    if(o) {
        for(prop in config) {
            if(o[prop] === undefined) {
                o[prop] = config[prop];
            }
        }
    }

    return o;
};

// }}}

