// {{{ NX.$e

/**
 * @class NX
 */

/**
 * @method $e
 *
 * ロケール応じたメッセージを取得します。
 *
 * @param {Object} key メッセージキー
 * @return {String} メッセージ
 */
module.exports = function(key) {

    var arg = [NX.$locale[key]];

    NX.iterate(arguments, function(item, i) {
        if(i > 0) {
            arg.push(item);
        }
    });

    return NX.String.format.apply(this, arg);
};

// }}}

