// {{{ NX.isDate

/**
 * @class NX
 */

/**
 * @method isDate
 *
 * テスト対象が日付型の場合はtrue、そうでない場合はfalseを返します。
 *
 * @param {Mixed} value テストするオブジェクト
 * @return {Boolean}
 */
module.exports = function(value) {
    return toString.call(value) === '[object Date]';
};

