// {{{ NX.isIterable

/**
 * @class NX
 */

/**
 * @method isIterable
 *
 * テスト対象がイテレート可能な場合はtrue、そうでない場合はfalseを返します。
 *
 * @param {Mixed} value テストするオブジェクト
 * @return {Boolean}
 */
module.exports = function(value) {
    return (
        (value && typeof value !== 'string')
        ? value.length !== undefined
        : false
    );
};

// }}}

