// {{{ NX.isNumber

/**
 * @class NX
 */

/**
 * @method isNumber
 *
 * テスト対象が数値型の場合はtrue、そうでない場合はfalseを返します。
 *
 * @param {Mixed} value テストするオブジェクト
 * @return {Boolean}
 */
module.exports = function(value) {
    return typeof value === 'number' && isFinite(value);
};

// }}}

