// {{{ NX.isNumeric

/**
 * @class NX
 */

/**
 * @method isNumeric
 *
 * テスト対象が数字の場合はtrue、そうでない場合はfalseを返します。
 *
 * @param {Mixed} value テストするオブジェクト <br />**例**: 1, '1', '2.34' はtrue
 * @return {Boolean}
 */
module.exports = function(value) {
    return !isNaN(parseFloat(value)) && isFinite(value);
};

// }}}

