// {{{ NX.isPrimitive

/**
 * @class NX
 */

/**
 * @method isPrimitive
 *
 * テスト対象が文字列型か数値型か真偽型の場合はtrue、そうでない場合はfalseを返します。
 *
 * @param {Mixed} value テストするオブジェクト
 * @return {Boolean}
 */
module.exports = function(value) {

    var type = typeof value;

    return (
        type === 'string' ||
        type === 'number' ||
        type === 'boolean'
    );
};

// }}}

