// {{{ NX.Object.getKey

/**
 * @class NX.Object
 */

/**
 * @method getKey
 *
 * "write it"
 *
 * @param o {Object} xxx
 * @return {Object}
 */
module.exports = function(object, value) {

    for(var property in object) {

        if(object.hasOwnProperty(property) && object[property] === value) {
            return property;
        }

    }

    return null;
};

// }}}

