// {{{ NX.String.urlAppend

/**
 * @class NX.String
 */

/**
 * @method urlAppend
 *
 * "write it"
 *
 * @param o {Object} xxx
 * @return {Object}
 */
module.exports = function(url, string) {

    if(!NX.isEmpty(string)) {
        return url + (url.indexOf('?') === -1 ? '?' : '&') + string;
    }

    return url;
};

// }}}

