// {{{ NX.util.Format.date

/**
 * @class NX.util.Format
 */

/**
 * @method date
 *
 * "write it"
 *
 * @param o {Object} xxx
 * @return {Object}
 */
module.exports = function(v, format) {

    if(!v) {
        return "";
    }

    if(!NX.isDate(v)) {
        v = new Date(Date.parse(v));
    }

    return NX.Date.dateFormat(v, format || NX.Date.defaultFormat);

};

// }}}

