// {{{ NX.util.Format.escapeRegex

/**
 * @class NX.util.Format
 */

/**
 * @method escapeRegex
 *
 * "write it"
 *
 * @param o {Object} xxx
 * @return {Object}
 */
module.exports = function(s) {
    return s.replace(/([\-.*+?\^${}()|\[\]\/\\])/g, "\\$1");
};

// }}}

