// {{{ NX.util.Format.nl2br

/**
 * @class NX.util.Format
 */

/**
 * @method nl2br
 *
 * "write it"
 *
 * @param o {Object} xxx
 * @return {Object}
 */
var nl2brRe = /\r?\n/g;

module.exports = function(v) {
    return NX.isEmpty(v) ? '' : v.replace(nl2brRe, '<br />');
};

// }}}

