// {{{ NX.util.Format.stripScripts

/**
 * @class NX.util.Format
 */

/**
 * @method stripScripts
 *
 * "write it"
 *
 * @param o {Object} xxx
 * @return {Object}
 */
var stripScriptsRe = /(?:<script.*?>)((\n|\r|.)*?)(?:<\/script>)/ig;

module.exports = function(v) {
    return !v ? v : String(v).replace(stripScriptsRe, "");
};

// }}}

