// {{{ requires

require('../../index.js');

// }}}
// {{{ describe

describe('NX.apply', function() {

    it('空のオブジェクトに値が適用されること', function() {

        var src = {}, ret;

        ret = NX.apply(src, {
            foo: 'bar'
        });

        src.should.equal(ret);
        src.foo.should.equal('bar');

    });

    it('デフォルト値が適用されること', function() {

        var src = {}, config, defaults, ret;

        config = {
            foo: 'bar'
        };

        defaults = {
            foo     : 'default value',
            hoge    : 'piyo'
        };

        ret = NX.apply(src, config, defaults);

        src.should.equal(ret);
        src.foo.should.equal('bar');
        src.hoge.should.equal('piyo');

    });

});

// }}}

