// {{{ requires

require('../../index.js');

// }}}
// {{{ describe

describe('NX.applyIf', function() {

    it('未定義のキーに新たなキーを追加することができること', function() {

        var src, ret;

        src = {
            hoge: 'piyo'
        };

        ret = NX.applyIf(src, {
            foo: 'bar'
        });

        src.should.equal(ret);
        src.foo.should.equal('bar');
        src.hoge.should.equal('piyo');

    });

    it('既に定義されているキーが上書きされないこと', function() {

        var src, config, ret;

        src = {
            foo: 'original'
        };

        config = {
            foo: 'bar'
        };

        ret = NX.applyIf(src, config);

        src.should.equal(ret);
        src.foo.should.equal('original');

    });

});

// }}}

